export function formatName(fullName: string): string {
    let parts = fullName.trim().split(" ");

    return parts
        .map((word, index) =>
            index === 0
                ? word.toUpperCase()  // Garder le premier mot en majuscules
                : word.charAt(0).toUpperCase() + word.slice(1).toLowerCase() // Majuscule sur la premiÃ¨re lettre uniquement
        )
        .join(" ");
}



