export const groupTasksByDate = (tasks: any[]) => {
    return tasks.reduce((acc, task) => {
        const { date, ...rest } = task;

        if (!acc[date]) {
            acc[date] = [];
        }

        acc[date].push(rest);

        return acc;
    }, {});
};
