export function groupByDay(events: any) {
    // 1. Trier les Ã©vÃ©nements par start_datetime
    const sortedEvents = events.sort((a, b) => (new Date(a.start_datetime) - new Date(b.start_datetime)));

    // 2. Regrouper les Ã©vÃ©nements triÃ©s par jour
    const groupedEvents = sortedEvents.reduce((groupedEvents, event) => {
        const date = new Date(event.start_datetime).toISOString().split('T')[0]; // RÃ©cupÃ¨re la date au format YYYY-MM-DD
        if (!groupedEvents[date]) {
            groupedEvents[date] = [];
        }
        groupedEvents[date].push(event);
        return groupedEvents;
    }, {});

    return groupedEvents;
}
export function formatDate(date: string) {
    const now = moment();
    const inputDate = moment(date);

    // VÃ©rifie si c'est aujourd'hui
    if (inputDate.isSame(now, 'day')) {
        // Si la diffÃ©rence est infÃ©rieure Ã  1 minute, retourne "Ã€ l'instant"
        if (now.diff(inputDate, 'minutes') < 1) {
            return "Ã€ l'instant";
        }
        // Sinon, retourne l'heure
        return inputDate.format('HH:mm');
    }

    // VÃ©rifie si c'est hier
    if (inputDate.isSame(now.subtract(1, 'day'), 'day')) {
        return 'Hier';
    }

    // Sinon, retourne la date au format DD/MM/YYYY
    return inputDate.format('DD/MM/YYYY');
}
export function removeHtmlTags(str: string) {
    return str.replace(/<[^>]*>/g, ''); // Remplace les balises HTML par une chaÃ®ne vide
}

